	public void setLog(String filename,FileHandler fh,Logger logger){
		try {
			fh=new FileHandler("log/log.txt",appendlog);
		} catch (IOException e) {
			e.printStackTrace();
		}
		fh.setFormatter(new SimpleFormatter());
		logger.addHandler(fh);
		logger.setLevel(Level.ALL);
	}

